mapper.insert:

insert into product (id, category_id, name, 
  subtitle, main_image, price, 
  stock, status, create_time, 
  update_time, sub_images, detail,is_hot,is_new,is_banner
  )
values (#{id,jdbcType=INTEGER}, #{categoryId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{subtitle,jdbcType=VARCHAR}, #{mainImage,jdbcType=VARCHAR}, #{price,jdbcType=DECIMAL}, 
  #{stock,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, now(),
now(), #{subImages,jdbcType=LONGVARCHAR}, #{detail,jdbcType=LONGVARCHAR},#{isHot},#{isNew},#{isBanner}
  )

mapper.select:
select *
from product


    and name = #{name}


    and id = #{id}


    and price = #{price}


    and category_id = #{categoryId}


      and  is_hot = #{isHot},


      and   is_new = #{isNew},


      and  is_banner = #{isBanner},

select * from product
and id = #{id}
and name like concat('%',#{name},'%')
